INSERT INTO `method` (`id`, `name`, `directive`, `description`, `url`) VALUES (3, 'IP Hash', 'ip_hash', 'The server to which a request is sent is determined from the client IP address. In this case, either the first three octets of the IPv4 address or the whole IPv6 address are used to calculate the hash value. The method guarantees that requests from the same address get to the same server unless it is not available.', 'https://nginx.org/en/docs/http/ngx_http_upstream_module.html?&_ga=2.116191366.1634715111.1589156948-54978449.1588995726#ip_hash')  ON DUPLICATE KEY UPDATE ID = 3;
